mapper.sql:
FROM album_course_type_relation
INNER JOIN album ON album_course_type_relation.album_id = album.album_id


        and album.album_id = #{albumId,jdbcType=INTEGER}


        and album.album_name like #{albumName,jdbcType=VARCHAR}


        and album.create_time >= #{createTimeStart,jdbcType=TIMESTAMP}


        and album.create_time <= #{createTimeEnd,jdbcType=TIMESTAMP}

mapper.select:
SELECT album.album_id

group by album.album_id
order by album.album_id
limit #{limit,jdbcType=INTEGER} offset #{offset,jdbcType=INTEGER}

    order by ${orderBy}
